import React, { useState } from 'react';
import NavBar from './NavBar';
import TopNav from './TopNav';
// import NavButton from './NavButton';
// import HoverableMenu from './HoverableMenu';

const cardData = [
	{
		title: 'Agent creation platform',
		description: 'Build agents with a powerful low-code tool',
	},
	{
		title: 'Knowledge base',
		description: 'Train agents on your data',
	},
	{
		title: 'Shareable prototypes',
		description: 'Create and share agent prototypes',
	},
	{
		title: 'Developer hand-Off',
		description: 'Hand-off agent designs to developers',
	},
];

function App() {
	const [isHovered, setIsHovered] = useState(null);

	return (
		<div className='app'>
			<TopNav />
			<NavBar />

			<nav className='navbar'>
				{cardData.map((card, index) => (
					<React.Fragment key={index}>
						<NavButton
							label={card.title}
							onMouseEnter={() => setIsHovered(index)}
						/>
						{isHovered === index && (
							<HoverableMenu
								title={card.title}
								description={card.description}
							/>
						)}
					</React.Fragment>
				))}
			</nav>
		</div>
	);
}

export default App;
