import './App.css';
import Menu from './Menu';
import NavBar from './NavBar';
import TopNav from './TopNav';
import { useState } from 'react';

// import Card from './Card';

const cardData = [
	{
		title: 'Agent creation platform',
		description: 'Build agents with a powerful low-code tool',
	},
	{
		title: 'Knowledge base',
		description: 'Train agents on your data',
	},
	{
		title: 'Shareable prototypes',
		description: 'Create and share agent prototypes',
	},
	{
		title: 'Developer hand-Off',
		description: 'Hand-off agent designs to developers',
	},
];

function App() {
	  const [isHovered, setIsHovered] = useState(false);

	return (
		<div className='App'>
			<TopNav />
			<NavBar />
			<div>
				{cardData.map((card, index) => (
					<Menu key={index} title={card.title} description={card.description} />
				))}
			</div>
			<div>
				<button
					onMouseEnter={() => setIsHovered(true)}
					onMouseLeave={() => setIsHovered(false)}>
					Show Card
				</button>
				{isHovered && (
					<div>
						{cardData.map((card, index) => (
							<Card
								key={index}
								title={card.title}
								description={card.description}
							/>
						))}
					</div>
				)}
			</div>
		</div>
	);
}

export default App;
